import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'


export const PokemonList = () => {
	const [pokemon, setPokemon] = useState([])
    const navigate = useNavigate()

	useEffect(() => {
		fetch(`https://pokeapi.co/api/v2/pokemon?limit=150/`)
			.then((res) => res.json())
			.then((pokemonArray) => {
				setPokemon(pokemonArray)
			})
	}, []) // An empty dependency array will watch for the initial render of the component and only run the callback on that  initial run.

	return (<>
		<div className='pokemon__container'>
			{pokemon.map((pokemonObj) => {
				return (  
					<div
						className='pokemon'
						key={`${pokemonObj.id}`}
					>
						<div className='pokemon-name'>
                            {pokemonObj.name}
						</div>
                        <div className='pokemon-img'>
                            <img class='pokemon-pic' src={pokemonObj.sprites['front_default']} />
						</div>
                        <div className='pokemon-type'>
                            {pokemonObj.types.map((type) => type.type.name).join(', ')}
                        </div>
                        <br></br>
					</div>
				)
			})}
		</div>
    </>
	)
}
