import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"

export const EditProfile = ({setEditClick, user}) =>{
    const localPokeUser = localStorage.getItem("pokefile_user")
    const pokeUserObject = JSON.parse(localPokeUser)
    const [teams, setTeams] = useState ([])
    const [chosenTeam, setChosenTeam] = useState ('')
    let navigate = useNavigate()
    const [userInfo, setUserInfo] = useState({
        name: "",
        email: "",
        profilePicture:"",
        aboutMe: "",
        teamId: 0
    })

    useEffect(() =>{
        fetch("http://localhost:8088/teams")
        .then(res => res.json())
        .then ((teamsArray) =>{
            setTeams(teamsArray)
        })}
    ,[])

    useEffect(() =>{
        setUserInfo(user)}
    ,[])

    const [feedback, setFeedback] = useState("")

    useEffect(() => {
        if (feedback !== "") {
            // Clear feedback to make entire element disappear after 3 seconds
            setTimeout(() => setFeedback(""), 3000);
        }
    }, [feedback])

    const updateUserInfo = () => {
        return fetch(`http://localhost:8088/users/${userInfo.id}`, {
                method: "PUT",
                headers:{
                    "Content-Type": "application/json"
                },
                    body: JSON.stringify(userInfo)
               })
                    .then(res => res.json())
                    .then(() => {
                        setFeedback("Profile Updated")
                    })
                    .then(() =>{
                        navigate(0)
                    })
    }

    const handleTeamChange = (event) => {
        setChosenTeam(event.target.value)
        const copy = {...user}
        copy.teamId = parseInt(event.target.value)
        setUserInfo(copy)
    }

    const handleClick = () =>{
        setEditClick(false)
    }

    const handleClick2 = () =>{
        setEditClick(false)
        updateUserInfo()

    }

    const updateUser = (evt) =>{
        const copy = {...user}
        copy[evt.target.id] = evt.target.value
        setUserInfo(copy)
    }

    return<>
            <div id="changeName-form2">
                <fieldset className="name-form">
                <button className="x-button" onClick={handleClick}> x </button>
                    <h3>What would you like to change?</h3>   
                    <div className="babies">
                        <label htmlFor="name"> Name: </label>
                        <input onChange={updateUser}
                            type="text" id="name" className="form-control"
                            placeholder={user[0].name} autoFocus />
                    </div>
                    <div className="babies">
                        <label htmlFor="email"> Email Address: </label>
                        <input onChange={updateUser}
                            type="email" id="email" className="form-control"
                            placeholder={user[0].email} />
                    </div>
                    <div className="babies">
                        <label htmlFor="profilePicture"> Profile Picture: </label>
                        <input onChange={updateUser}
                            type="text" id="profilePicture" className="form-control"
                            placeholder={user[0].profilePicture} />
                    </div>
                    <div className="babies">
                        <label htmlFor="aboutMe"> About Me: </label>
                        <input onChange={updateUser}
                            type="text" id="aboutMe" className="form-control"
                            placeholder={user[0].aboutMe} />
                    </div>
                    <div className="babies">
                        <label>Team:</label>
                        <select className="form-control" value={chosenTeam} onChange={handleTeamChange}>
                            <option hidden>Change Team?</option>
                            {teams.map((team) => (
                            <option key={team.id} value={team.id} id={team.id}>{team.name}</option>
                            ))}
                        </select>
                    </div>
                    <div className="buttons">

                        <button type="submit" className="button-85" onClick={handleClick2}> âœ“ </button>
                    </div>
                </fieldset>
            </div>
    </>
}
