import { useState, useEffect } from "react"
import { useNavigate  } from "react-router-dom"
import { Dropdown } from 'react-dropdown';

export const Register = (props) => {
    const [user, setUser] = useState({
        name: "",
        email: "",
        aboutMe: "",
        teamId: 0
    })
    const [teams, setTeams] = useState ([])
    let navigate = useNavigate()

    const useEffect = () =>{
        fetch("http://localhost:8088/teams")
        .then(res => res.json())
        .then ((teamsArray) =>{
            setTeams(teamsArray)
        })
    }

    const registerNewUser = () => {
        return fetch("http://localhost:8088/users", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(user)
        })
            .then(res => res.json())
            .then(createdUser => {
                if (createdUser.hasOwnProperty("id")) {
                    localStorage.setItem("pokefile_user", JSON.stringify({
                        id: createdUser.id,
                    }))

                    navigate("/")
                }
            })
    }

    const handleRegister = (e) => {
        e.preventDefault()
        return fetch(`http://localhost:8088/users?email=${user.email}`)
            .then(res => res.json())
            .then(response => {
                if (response.length > 0) {
                    // Duplicate email. No good.
                    window.alert("Account with that email address already exists")
                }
                else {
                    // Good email, create user.
                    registerNewUser()
                }
            })
    }

    const updateUser = (evt) => {
        const copy = {...user}
        copy[evt.target.id] = evt.target.value
        setUser(copy)
    }

    return (
        <main style={{ textAlign: "center" }}>
            <form className="form--login" onSubmit={handleRegister}>
                <h1 className="h3 mb-3 font-weight-normal">Please Register for Pokefile!</h1>
                <fieldset>
                    <label htmlFor="name"> Name: </label>
                    <input onChange={updateUser}
                           type="text" id="name" className="form-control"
                           placeholder="what do you want to be called?" required autoFocus />
                </fieldset>
                <fieldset>
                    <label htmlFor="email"> Email Address: </label>
                    <input onChange={updateUser}
                        type="email" id="email" className="form-control"
                        placeholder="email you'll use to login!" required />
                </fieldset>
                <fieldset>
                    <label htmlFor="aboutMe"> About Me: </label>
                    <input onChange={updateUser}
                        type="text" id="aboutMe" className="form-control"
                        placeholder="this is for your profile" required />
                </fieldset>
                <fieldset>
                <Dropdown options={teams.name} 
                    value={teams.id}
                    onChange={this._onSelect} />
                </fieldset>
                <fieldset>
                    <button type="submit"> Register </button>
                </fieldset>
            </form>
        </main>
    )
}
